  `news_id` int(11) NOT NULL AUTO_INCREMENT,
  `news_category_id` varchar(64),
  `news_producer_id` int(11) ,
  `news_title` varchar(256) DEFAULT NULL,
  `news_url` varchar(2048) DEFAULT NULL,
  `news_abstract` varchar(2048) DEFAULT NULL,
  `news_content` text,
